package absorbingChain.utilities;/*	JRandom Library		by Julian Devlin 6/30/97*///package julian.util;public class JRandom extends java.util.Random {		public JRandom() {		super();	}	// Return random bounded integer	public int nextInt(int range) {		return nextInt(1, range);	}		// Return random bounded integer	public int nextInt(int low, int high) {		float randFloat;		int randInt;		randFloat = nextFloat();		randInt = (int) Math.floor(randFloat * (high - low + 1)) + low;		return randInt;	}		public float nextFloat(float range) {		return nextFloat(0, range);	}		public float nextFloat(float low, float high) {		float randFloat;		randFloat = nextFloat();		randFloat = randFloat * (high - low) + low;		return randFloat;	}}